    :new.value_check_on := sysdate;                                                                                                                                                                                                                                                                                                                                                                                                                                                                                 
  end if;                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                           
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                    
  -- validation flags un-checked                                                                                                                                                                                                                                                                                                                                                                                                                                                                                    
  if :new.qty_check = 'N' and :old.qty_check = 'Y' then                                                                                                                                                                                                                                                                                                                                                                                                                                                             
    :new.qty_check_by := null;                                                                                                                                                                                                                                                                                                                                                                                                                                                                                      
    :new.qty_check_on := null;                                                                                                                                                                                                                                                                                                                                                                                                                                                                                      
  end if;                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                           
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                    
  if  :new.quality_check = 'N' and :old.quality_check = 'Y' then                                                                                                                                                                                                                                                                                                                                                                                                                                                    
