  if  :new.value_check = 'N' and :old.value_check = 'Y' then                                                                                                                                                                                                                                                                                                                                                                                                                                                        
    :new.value_check_by := null;                                                                                                                                                                                                                                                                                                                                                                                                                                                                                    
    :new.value_check_on := null;                                                                                                                                                                                                                                                                                                                                                                                                                                                                                    
  end if;                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                           
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                    
  :new.modifiedon := sysdate;                                                                                                                                                                                                                                                                                                                                                                                                                                                                                       
  :new.modifiedby := pk_session.getUser;                                                                                                                                                                                                                                                                                                                                                                                                                                                                            
